import React, { useEffect, useState } from 'react'
import { HiOutlineUser } from "react-icons/hi";

const Navbtn = (props: any) => {
  return (
      <button className={"px-3 py-3 mx-5 my-2 bg-white rounded-lg hover:bg-gray-200 focus:outline-none " + props.class} onClick={props.onClick}>
        {props.text}
      </button>
  )

}

const ProfileDropdown = (props: any) => {
  if(props.userLogged) {
    return (
      <div className={"absolute flex flex-col mt-2 mr-2 w-48 right-0 border-2 bg-gray-200 rounded-lg " + props.class}>
        <h3>Profile Settings</h3>
        <Navbtn text="Profile" class="border-2"/>
        <Navbtn text="Log out"/>
      </div>
    )
  }
  return (
    <div className={"absolute flex flex-col mt-2 mr-2 w-48 right-0 border-2 bg-gray-100 rounded-lg " + props.class}>
      <h1 className="mx-1 my-1 text-xl">Profile Settings</h1>
      <Navbtn text="Log in" class="border-2"/>
    </div>
  )
}

const AppBar = (props: any) => {
   const [dropdownProfileclass, setDropdownProfileClass] = useState("invisible");

  return (
    <div className="relative w-max bg-white">
      <div className="flex h-16 flex-row justify-end">
        <img className="absolute left-0 ml-5 h-16" src="https://cdn.logo.com/hotlink-ok/logo-social.png"/>
        {(
          <>
            <Navbtn text="Scopri" />
            <Navbtn text="Recensioni" />
            <Navbtn text="Classifiche" />
            <button className="px-3 py-3 mx-5 my-2 rounded-lg hover:bg-gray-100 focus:outline-none" 
                onFocus={() => {setDropdownProfileClass("visible")}} 
                onBlur={() => setTimeout(() => setDropdownProfileClass("invisible"), 180)}>
              <HiOutlineUser className="text-2xl"/>
            </button>
          </>
        )}
      </div>
      <ProfileDropdown userLogged={props.userLogged} class={dropdownProfileclass}/>
    </div>
  )
}

export default AppBar;
