import React, { useEffect, useRef } from 'react'

const Sidebtn = (props: any) => {
    return (
        <button className={"z-30 text-2xl w-full py-4 hover:bg-gray-200 focus:outline-none " + props.class}>
            {props.text}
        </button>
    )
}


const SideBar = (props: any) => {

    const wrapperRefSideBar = useRef(null);
    useOutsideAlerter(wrapperRefSideBar, "SideBar");

    function useOutsideAlerter(ref: any, component: String) {
        useEffect(() => {
            function handleClickOutside(event: any) {
                if(component === "SideBar") {
                    if(ref.current && !ref.current.contains(event.target)){
                        setTimeout(() => props.setSidebarEna, 100);
                    }
                }
            }

            // Bind the event listener
            document.addEventListener("mousedown", handleClickOutside);
            return () => {
                // Unbind the event listener on clean up
                document.removeEventListener("mousedown", handleClickOutside);
            };
    }, [ref]);}

  return (
    <div ref={wrapperRefSideBar} className={"z-30 fixed rounded-lg right-0 pt-16 w-full bg-white border shadow-xl transition-transform duration-300 ease-in-out " + props.class}>
        <ul className="flex flex-col divide-y">
            {(
                <>
                    <Sidebtn text="Scopri"/>
                    <Sidebtn text="Recensioni"/>
                    <Sidebtn text="Classifiche"/>
                    <Sidebtn text="Profile" class="mt-16"/>
                </>
            )}
        </ul>
    </div>
  )
}

export default SideBar
